var http = require("http");
var fs = require("fs");
var qs = require('querystring');
var json = {};
var thirdArg = process.argv[2];
var forthArg = process.argv[3];

console.log(thirdArg);
console.log(forthArg);
//$ node test-client.js --create --message="Relationship status: пътувам с автобус"
//    <RESPONSE FROM API WITH CHIRP ID>

var user = {
    username: 'The Reddest',
    email: 'brandon@switchonthecode.com',
    firstName: 'Brandon',
    lastName: 'Cannaday'
};

var userString = JSON.stringify(user);

var headers = {
    'Content-Type': 'application/json',
    'Content-Length': userString.length
};

var options = {
    host: 'localhost',
    port: 8080,
    path: '/chirp',
    method: 'POST',
    headers: headers
};

// Setup the request.  The options parameter is
// the object we defined above.
var req = http.request(options, function(res) {
    res.setEncoding('utf-8');

    var responseString = '';

    res.on('data', function(data) {
        responseString += data;
    });

    res.on('end', function() {
        var resultObject = JSON.parse(responseString);
        console.log(resultObject);
    });
});

req.on('error', function(e) {
    console.log(e);
});
req.write(userString);
req.end();

